mapper.select:
SELECT f.file_id, f.student_id, s.name as student_name, f.file_name, f.original_name, f.file_size, f.file_type, f.file_category, f.upload_time, f.remarks FROM file_upload f LEFT JOIN Student s ON f.student_id = s.student_id WHERE f.is_deleted = 0
AND f.student_id = #{req.studentId}
AND f.original_name LIKE CONCAT('%', #{req.fileName}, '%')
AND f.file_category = #{req.fileCategory}
AND f.file_type = #{req.fileType}
ORDER BY f.upload_time DESC

SELECT * FROM file_upload WHERE file_id IN
#{fileId}
AND student_id = #{studentId} AND is_deleted = 0
